import styled from 'styled-components'
import tw from 'twin.macro'

const StyledForm = styled.main.attrs({
    className: 'flex flex-col mx-auto h-screen justify-center items-center bg-gray-100 px-50'
})
    ` 
    & {
        label{
            ${tw`text-pink-900`}
        }

        div{
            ${tw`text-2xl mb-4 text-pink-700`}
        }

        form {
           ${tw`bg-white text-center rounded py-8 px-3 shadow max-w-lg m-auto`} 
        }

        input {
            ${tw`border-gray-300 mb-4 w-full border-solid border rounded py-2 px-4`}
        }

        button {
            ${tw`bg-pink-500 hover:bg-pink-700 text-white font-bold border border-pink-700 rounded text-xs mr-4 p-1 w-2/5`}
        }
    }
`

export default StyledForm
